import React, {useState} from 'react';
import {
    MDBBtn,
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBCard,
    MDBCardBody,
    MDBInput,
    MDBIcon,
    MDBCheckbox, MDBValidationItem, MDBValidation, MDBSpinner
}
    from 'mdb-react-ui-kit';
import './Login.css';
import {Link} from "react-router-dom";
import axios from "axios";
import Loading from "../common/Loading";

function loginClick() {
    document.location.href = '/'
}

/**
 * 로그인 기능
 * @returns {JSX.Element}
 * @constructor
 */
function Login() {
    const loginForm = {
        inputEmail: '',
        inputPw: '',
        userNm: '',
        userPw: '',
        userEmail: '',
        userPwCheck: ''
    };

    // 로그인 입력값
    const [loginInputValue, setLoginInputValue] = useState(loginForm);
    // 회원가입 입력값
    const [singUpInputValue, setSignUpInputValue] = useState(loginForm);

    // const {inputEmail, inputPw} = loginInputValue;

    // const [showLoading, setShowLoading] = useState(false);

    // 로딩바 표시 상태값
    const [loading, setLoading] = useState(false);

    // 회원가입 버튼 상태값
    const [signUpClickEv, setSignUpClickEv] = useState(false);

    const [changePwClickEv, setChangePwClickEv] = useState(false);

    // 로그인 입력값 변경 감지
    const handleLoginInputValue = (e) => {
        setLoginInputValue({
            ...loginInputValue,
            [e.target.name]: e.target.value
        });
    }

    // 회원가입 입력값 변경 감지
    const handleSignUpInputValue = (e) => {
        setSignUpInputValue({
            ...singUpInputValue,
            [e.target.name]: e.target.value
        });
    }

    // 로그인 버튼 클릭 이벤트
    const loginClick = () => {
        console.log(loginInputValue);
        const headers = {
            'Content-Type' : 'application/json'
        }
        // setShowLoading(true);
        setLoading(true);
        axios.post('/api/user/login', {
                'userId': loginInputValue.inputEmail,
                'userPw': loginInputValue.inputPw
            },
            {
            headers: headers
            }
        )
        .then(res => {
            console.log(res)
            console.log('res.data :: ', res.data)
            // setShowLoading(false);

            // setLoading(false);
            // if(res.data.userId === undefined){
            //     // id 일치하지 않는 경우 userId = undefined, msg = '입력하신 id 가 일치하지 않습니다.'
            //     console.log('======================',res.data.msg)
            //     alert('입력하신 id 가 일치하지 않습니다.')
            // } else if(res.data.userId === null){
            //     // id는 있지만, pw 는 다른 경우 userId = null , msg = undefined
            //     console.log('======================','입력하신 비밀번호 가 일치하지 않습니다.')
            //     alert('입력하신 비밀번호 가 일치하지 않습니다.')
            // } else if(res.data.userId === inputId) {
            //     // id, pw 모두 일치 userId = userId1, msg = undefined
            //     console.log('======================','로그인 성공')
            //     sessionStorage.setItem('user_id', inputId)
            // }
            // 작업 완료 되면 페이지 이동(새로고침)
            // setLoading(false);
            if (res.data == 'Y') {
                // document.location.href = '/'
            }
        })
        .catch()

        // document.location.href = '/'

    }

    // 회원가입 버튼 클릭
    const signUpClick = () => {
        setSignUpClickEv(true);
    }


    // 가입신청 버튼 클릭
    const requestSignUp = () => {

    }

    // 패스워드 찾기 버튼 클릭
    const changePw = () => {
        setChangePwClickEv(true);
        setSignUpClickEv(false);
    }

    const requestChangePw = () => {

    }

    // 가입신청 취소
    const cancelClick = () => {
        setSignUpClickEv(false);
        setChangePwClickEv(false);
    }

    const [formValue, setFormValue] = useState({
        fname: 'Mark',
        lname: 'Otto',
        email: '',
        city: '',
        state: '',
        zip: '',
    });

    const onChange = (e: any) => {
        setFormValue({ ...formValue, [e.target.name]: e.target.value });
    };

    return (
        <MDBValidation className='row g-3'>
            <MDBValidationItem tooltip className='col-md-4'>
                <MDBInput
                    value={formValue.fname}
                    name='fname'
                    onChange={onChange}
                    id='validationCustom01'
                    required
                    label='First name'
                />
            </MDBValidationItem>
            <MDBValidationItem tooltip className='col-md-4'>
                <MDBInput
                    value={formValue.lname}
                    name='lname'
                    onChange={onChange}
                    id='validationCustom02'
                    required
                    label='Last name'
                />
            </MDBValidationItem>
            <MDBValidationItem tooltip feedback='Please choose a username.' invalid className='col-md-4'>
                <MDBInputGroup textBefore='@'>
                    <input
                        type='text'
                        className='form-control'
                        id='validationCustomUsername'
                        placeholder='Username'
                        required
                    />
                </MDBInputGroup>
            </MDBValidationItem>
            <MDBValidationItem tooltip className='col-md-6' feedback='Please provide a valid city.' invalid>
                <MDBInput
                    value={formValue.city}
                    name='city'
                    onChange={onChange}
                    id='validationCustom03'
                    required
                    label='City'
                />
            </MDBValidationItem>
            <MDBValidationItem tooltip className='col-md-6' feedback='Please provide a valid zip.' invalid>
                <MDBInput
                    value={formValue.zip}
                    name='zip'
                    onChange={onChange}
                    id='validationCustom05'
                    required
                    label='Zip'
                />
