import React, {useState} from 'react';
import {
    MDBBtn,
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBCard,
    MDBCardBody,
    MDBInput,
    MDBIcon,
    MDBCheckbox, MDBValidationItem, MDBValidation, MDBSpinner
}
    from 'mdb-react-ui-kit';
import './Login.css';
import {Link} from "react-router-dom";
import axios from "axios";
import Loading from "../common/Loading";

function loginClick() {
    document.location.href = '/'
}

/**
 * 로그인 기능
 * @returns {JSX.Element}
 * @constructor
 */
function Login() {
    const loginForm = {
        inputEmail: '',
        inputPw: '',
        userNm: '',
        userPw: '',
        userEmail: '',
        userPwCheck: ''
    };

    // 로그인 입력값
    const [loginInputValue, setLoginInputValue] = useState(loginForm);
    // 회원가입 입력값
    const [singUpInputValue, setSignUpInputValue] = useState(loginForm);

    // const {inputEmail, inputPw} = loginInputValue;

    // const [showLoading, setShowLoading] = useState(false);

    // 로딩바 표시 상태값
    const [loading, setLoading] = useState(false);

    // 회원가입 버튼 상태값
    const [signUpClickEv, setSignUpClickEv] = useState(false);

    const [changePwClickEv, setChangePwClickEv] = useState(false);

    // 로그인 입력값 변경 감지
    const handleLoginInputValue = (e) => {
        setLoginInputValue({
            ...loginInputValue,
            [e.target.name]: e.target.value
        });
    }

    // 회원가입 입력값 변경 감지
    const handleSignUpInputValue = (e) => {
        setSignUpInputValue({
            ...singUpInputValue,
            [e.target.name]: e.target.value
        });
    }

    // 로그인 버튼 클릭 이벤트
    const loginClick = () => {
        console.log(loginInputValue);
        const headers = {
            'Content-Type' : 'application/json'
        }
        // setShowLoading(true);
        setLoading(true);
        axios.post('/api/user/login', {
                'userId': loginInputValue.inputEmail,
                'userPw': loginInputValue.inputPw
            },
            {
            headers: headers
            }
        )
        .then(res => {
            console.log(res)
            console.log('res.data :: ', res.data)
            // setShowLoading(false);

            // setLoading(false);
            // if(res.data.userId === undefined){
            //     // id 일치하지 않는 경우 userId = undefined, msg = '입력하신 id 가 일치하지 않습니다.'
            //     console.log('======================',res.data.msg)
            //     alert('입력하신 id 가 일치하지 않습니다.')
            // } else if(res.data.userId === null){
            //     // id는 있지만, pw 는 다른 경우 userId = null , msg = undefined
            //     console.log('======================','입력하신 비밀번호 가 일치하지 않습니다.')
            //     alert('입력하신 비밀번호 가 일치하지 않습니다.')
            // } else if(res.data.userId === inputId) {
            //     // id, pw 모두 일치 userId = userId1, msg = undefined
            //     console.log('======================','로그인 성공')
            //     sessionStorage.setItem('user_id', inputId)
            // }
            // 작업 완료 되면 페이지 이동(새로고침)
            // setLoading(false);
            if (res.data == 'Y') {
                // document.location.href = '/'
            }
        })
        .catch()

        // document.location.href = '/'

    }

    // 회원가입 버튼 클릭
    const signUpClick = () => {
        setSignUpClickEv(true);
    }


    // 가입신청 버튼 클릭
    const requestSignUp = () => {

    }

    // 패스워드 찾기 버튼 클릭
    const changePw = () => {
        setChangePwClickEv(true);
        setSignUpClickEv(false);
    }

    const requestChangePw = () => {

    }

    // 가입신청 취소
    const cancelClick = () => {
        setSignUpClickEv(false);
        setChangePwClickEv(false);
    }

    return (
        <MDBValidation>
        <MDBContainer fluid>
            <div>
                {loading ? <Loading /> : null}
            </div>

            <MDBRow className='d-flex justify-content-center align-items-center h-100'>
                <MDBCol col='12'>

                    <MDBCard className='bg-white my-5 mx-auto' style={{borderRadius: '1rem', maxWidth: '500px'}}>

                        {/*------------ 로그인 입력 form start------------*/}
                        { signUpClickEv || changePwClickEv ? null :
                            <MDBCardBody className='p-5 w-100 d-flex flex-column'>
                                <h2 className="fw-bold mb-2 text-center">Sign in</h2>
                                {/*<p>Please enter your login and password!</p>*/}

                                <MDBValidationItem tooltip className='col-md-6' feedback='Please provide a valid city.' invalid>
                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="이메일 주소를 입력해주세요."
                                          label='Email address'
                                          id='formControlEmail'
                                          type='email'
                                          size="lg"
                                          name='inputEmail'
                                          value={ loginInputValue.inputEmail }
                                          onChange={ handleLoginInputValue }
                                          required/>
                                </MDBValidationItem>

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="비밀번호를 입력해주세요."
                                          label='Password'
                                          id='formControlPw'
                                          type='password'
                                          size="lg"
                                          name='inputPw'
                                          value={ loginInputValue.inputPw }
                                          onChange={ handleLoginInputValue }
                                          required/>

                                <MDBCheckbox name='flexCheck' id='flexCheckDefault' className='mb-4'
                                             label='Remember password'/>

                                <MDBBtn size='lg'
                                        style={{ backgroundColor: '#26B7E6' }}
                                        onClick={ loginClick }>
                                    {/*{showLoading ? <MDBSpinner size='sm' role='status' tag='span' className='me-2' /> : null}*/}
                                    Login
                                </MDBBtn>


                                <hr className="my-4"/>

                                <div className="find-btn">
                                    <MDBBtn className='me-1'
                                            style={{ backgroundColor: '#26B7E6' }}
                                            onClick={ signUpClick }>
                                        회원가입
                                    </MDBBtn>

                                    <MDBBtn className='me-1'
                                            color='secondary'
                                            onClick={ changePw }>
                                        패스워드 찾기
                                    </MDBBtn>
                                    {/*<MDBBtn className='mx-2' color='tertiary' rippleColor='light'>*/}
                                    {/*    Tertiary*/}
                                    {/*</MDBBtn>*/}
                                </div>
                            </MDBCardBody>
                        }
                        {/*------------ 로그인 입력 form end------------*/}

                        {/*------------ 회원가입 입력 form start------------*/}
                        { signUpClickEv ?
                            <MDBCardBody className='p-5 w-100 d-flex flex-column'>
                                <h2 className="fw-bold mb-2 text-center">Sign Up</h2>

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="이름을 입력해주세요."
                                          label='Name'
                                          id='formControlUserNm'
                                          type='name'
                                          size="lg"
                                          name='userNm'
                                          value={ singUpInputValue.userNm }
                                          onChange={ handleSignUpInputValue }
                                          required/>

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="아이디로 사용할 이메일 주소를 입력해주세요."
                                          label='Email'
                                          id='formControlUserEmail'
                                          type='email'
                                          size="lg"
                                          name='userEmail'
                                          value={ singUpInputValue.userEmail }
                                          onChange={ handleSignUpInputValue }
                                          required/>

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="비밀번호를 입력해주세요."
                                          label='비밀번호'
                                          id='formControlUserPw'
                                          type='password'
                                          size="lg"
                                          name='userPw'
                                          value={ singUpInputValue.userPw }
                                          onChange={ handleSignUpInputValue }
                                          required/>

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="비밀번호 확인"
                                          label='비밀번호 확인'
                                          id='formControlUserPwCheck'
                                          type='password'
                                          size="lg"
                                          name='userPwCheck'
                                          value={ singUpInputValue.userPwCheck }
                                          onChange={ handleSignUpInputValue }
                                          required/>

                                <MDBBtn size='lg'
                                        style={{ backgroundColor: '#26B7E6' }}
                                        onClick={ requestSignUp }>
                                    {/*{showLoading ? <MDBSpinner size='sm' role='status' tag='span' className='me-2' /> : null}*/}
                                    가입신청
                                </MDBBtn>

                                <hr className="my-4"/>

                                <div className="find-btn">
                                    <MDBBtn className='me-1'
                                            // style={{ backgroundColor: '#26B7E6' }}
                                            color="secondary"
                                            onClick={ cancelClick }>
                                        이전으로
                                    </MDBBtn>
                                    {/*<MDBBtn className='mx-2' color='tertiary' rippleColor='light'>*/}
                                    {/*    Tertiary*/}
                                    {/*</MDBBtn>*/}
                                </div>

                            </MDBCardBody> : null
                        }
                        {/*------------ 회원가입 입력 form end------------*/}


                        {/*------------ 패스워드 찾기 입력 form start------------*/}
                        { changePwClickEv ?
                            <MDBCardBody className='p-5 w-100 d-flex flex-column'>
                                <h2 className="fw-bold mb-2 text-center">Sign Up</h2>

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="이름을 입력해주세요."
                                          label='Name'
                                          id='formControlUserNm'
                                          type='name'
                                          size="lg"
                                          name='userNm'
                                          value={ singUpInputValue.userNm }
                                          onChange={ handleSignUpInputValue }
                                          required/>

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="이메일 주소를 입력해주세요."
                                          label='Email'
                                          id='formControlUserEmail'
                                          type='email'
                                          size="lg"
                                          name='userEmail'
                                          value={ singUpInputValue.userEmail }
                                          onChange={ handleSignUpInputValue }
                                          required/>


                                <MDBBtn size='lg'
                                        style={{ backgroundColor: '#26B7E6' }}
                                        onClick={ requestChangePw }>
                                    {/*{showLoading ? <MDBSpinner size='sm' role='status' tag='span' className='me-2' /> : null}*/}
                                    패스워드 찾기
                                </MDBBtn>

                                <hr className="my-4"/>

                                <div className="find-btn">
                                    <MDBBtn className='me-1'
                                        // style={{ backgroundColor: '#26B7E6' }}
                                            color="secondary"
                                            onClick={ cancelClick }>
                                        이전으로
                                    </MDBBtn>
                                    {/*<MDBBtn className='mx-2' color='tertiary' rippleColor='light'>*/}
                                    {/*    Tertiary*/}
                                    {/*</MDBBtn>*/}
                                </div>

                            </MDBCardBody> : null
                        }
                        {/*------------ 패스워드 찾기 입력 form end------------*/}
                    </MDBCard>

                </MDBCol>
            </MDBRow>

        </MDBContainer>
        </MDBValidation>
    );
}



export default Login;
