import React, {useState} from 'react';
import {
    MDBBtn,
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBCard,
    MDBCardBody,
    MDBInput,
    MDBIcon,
    MDBCheckbox, MDBValidationItem, MDBValidation, MDBSpinner
}
    from 'mdb-react-ui-kit';
import './Login.css';
import {Link} from "react-router-dom";
import axios from "axios";
import Loading from "../common/Loading";
import {Button, Modal} from "react-bootstrap";

function loginClick() {
    document.location.href = '/'
}

/**
 * 로그인 기능
 * @returns {JSX.Element}
 * @constructor
 */
function Login() {
    const loginForm = {
        inputEmail: '',
        inputPw: '',
        userNm: '',
        userPw: '',
        userEmail: '',
        userPwCheck: '',
        checkNumber: ''
    };

    // 로그인 입력값
    const [loginInputValue, setLoginInputValue] = useState(loginForm);
    // 회원가입 입력값
    const [singUpInputValue, setSignUpInputValue] = useState(loginForm);

    // const {inputEmail, inputPw} = loginInputValue;

    // const [showLoading, setShowLoading] = useState(false);

    // 로딩바 표시 상태값
    const [loading, setLoading] = useState(false);

    // 회원가입 버튼 상태값
    const [signUpClickEv, setSignUpClickEv] = useState(false);
    const [checkNumberBox, setCheckNumberBox] = useState(false);

    const [changePwClickEv, setChangePwClickEv] = useState(false);

    // 모달 초기화
    const [show, setShow] = useState(false);
    const handleClose = (cancelYn) => {
        setShow(false);
        if(cancelYn) {
            cancelClick();
        }
    }
    const handleShow = () => setShow(true);

    // 입력 Form 초기화
    const formInit = () => {
        setLoginInputValue(loginForm);
        setSignUpInputValue(loginForm);
    }

    // 로그인 입력값 변경 감지
    const handleLoginInputValue = (e) => {
        setLoginInputValue({
            ...loginInputValue,
            [e.target.name]: e.target.value
        });
    }

    // 회원가입 입력값 변경 감지
    const handleSignUpInputValue = (e) => {
        setSignUpInputValue({
            ...singUpInputValue,
            [e.target.name]: e.target.value
        });
    }

    // 로그인 버튼 클릭 이벤트
    const loginClick = () => {
        console.log(loginInputValue);
        const headers = {
            'Content-Type' : 'application/json'
        }
        // setShowLoading(true);
        setLoading(true);
        axios.post('/api/user/login', {
                'userId': loginInputValue.inputEmail,
                'userPw': loginInputValue.inputPw
            },
            {
            headers: headers
            }
        )
        .then(res => {
            console.log(res)
            console.log('res.data :: ', res.data)
            // setShowLoading(false);

            // setLoading(false);
            // if(res.data.userId === undefined){
