import React, {useState} from 'react';
import {
    MDBBtn,
    MDBContainer,
    MDBRow,
    MDBCol,
    MDBCard,
    MDBCardBody,
    MDBInput,
    MDBIcon,
    MDBCheckbox, MDBValidationItem, MDBValidation, MDBSpinner
}
    from 'mdb-react-ui-kit';
import './Login.css';
import {Link} from "react-router-dom";
import axios from "axios";
import Loading from "../common/Loading";

function loginClick() {
    document.location.href = '/'
}

/**
 * 로그인 기능
 * @returns {JSX.Element}
 * @constructor
 */
function Login() {

    // 이메일 입력 상태값
    const [inputEmail, setInputEmail] = useState('');
    // 패스워드 입력 상태값
    const [inputPw, setInputPw] = useState('');
    // const [showLoading, setShowLoading] = useState(false);

    // 로딩바 표시 상태값
    const [loading, setLoading] = useState(false);

    // 회원가입 버튼 상태값
    const [signUpClickEv, setSignUpClickEv] = useState(false);


    const handleInputEmail = (e) => {
        setInputEmail(e.target.value)
    }

    const handleInputPw = (e) => {
        setInputPw(e.target.value)
    }

    const loginClick = () => {
        if (inputEmail == null) {

        }

        const headers = {
            'Content-Type' : 'application/json'
        }
        // setShowLoading(true);
        setLoading(true);
        axios.post('/api/user/login', {
                'userId': inputEmail,
                'userPw': inputPw
            },
            {
            headers: headers
            }
        )
        .then(res => {
            console.log(res)
            console.log('res.data :: ', res.data)
            // setShowLoading(false);

            // setLoading(false);
            // if(res.data.userId === undefined){
            //     // id 일치하지 않는 경우 userId = undefined, msg = '입력하신 id 가 일치하지 않습니다.'
            //     console.log('======================',res.data.msg)
            //     alert('입력하신 id 가 일치하지 않습니다.')
            // } else if(res.data.userId === null){
            //     // id는 있지만, pw 는 다른 경우 userId = null , msg = undefined
            //     console.log('======================','입력하신 비밀번호 가 일치하지 않습니다.')
            //     alert('입력하신 비밀번호 가 일치하지 않습니다.')
            // } else if(res.data.userId === inputId) {
            //     // id, pw 모두 일치 userId = userId1, msg = undefined
            //     console.log('======================','로그인 성공')
            //     sessionStorage.setItem('user_id', inputId)
            // }
            // 작업 완료 되면 페이지 이동(새로고침)
            // setLoading(false);
            if (res.data == 'Y') {
                // document.location.href = '/'
            }
        })
        .catch()

        // document.location.href = '/'

    }

    const signInClick = () => {
        setSignUpClickEv(true);
    }

    const signInCancelClick = () => {
        setSignUpClickEv(false);
    }

    return (
        <MDBContainer fluid>
            <div>
                {loading ? <Loading /> : null}
            </div>
            <MDBRow className='d-flex justify-content-center align-items-center h-100'>
                <MDBCol col='12'>

                    <MDBCard className='bg-white my-5 mx-auto' style={{borderRadius: '1rem', maxWidth: '500px'}}>

                        { signUpClickEv ? null :
                            <MDBCardBody className='p-5 w-100 d-flex flex-column'>
                                <h2 className="fw-bold mb-2 text-center">Sign in</h2>
                                {/*<p className="text-white-50 mb-3">Please enter your login and password!</p>*/}

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="이메일 주소를 입력해주세요."
                                          label='Email address'
                                          id='formControlEmail'
                                          type='email'
                                          size="lg"
                                          name='input_email'
                                          value={inputEmail}
                                          onChange={handleInputEmail}
                                          required/>

                                <MDBInput wrapperClass='mb-4 w-100'
                                          placeholder="비밀번호를 입력해주세요."
                                          label='Password'
                                          id='formControlPw'
                                          type='password'
                                          size="lg"
                                          name='input_pw'
                                          value={inputPw}
                                          onChange={handleInputPw}
                                          required/>

                                <MDBCheckbox name='flexCheck' id='flexCheckDefault' className='mb-4'
                                             label='Remember password'/>

                                <MDBBtn size='lg'
                                        style={{backgroundColor: '#26B7E6'}}
                                        onClick={loginClick}>
                                    {/*{showLoading ? <MDBSpinner size='sm' role='status' tag='span' className='me-2' /> : null}*/}
                                    Login
                                </MDBBtn>


                                <hr className="my-4"/>

                                <div className="find-btn">
                                    <MDBBtn className='me-1'
                                            style={{backgroundColor: '#26B7E6'}}
                                            onClick={signInClick}>
                                        회원가입
                                    </MDBBtn>

                                    <MDBBtn className='me-1'
                                            color='secondary'>
                                        패스워드 찾기
                                    </MDBBtn>
                                    {/*<MDBBtn className='mx-2' color='tertiary' rippleColor='light'>*/}
                                    {/*    Tertiary*/}
                                    {/*</MDBBtn>*/}
                                </div>
                            </MDBCardBody>
                        }
                    </MDBCard>

                </MDBCol>
            </MDBRow>

        </MDBContainer>
    );
}



export default Login;
